import React, { useState } from 'react';
import './PlantDetails.css';

  function PlantDetails({ plant, onCancel }) {
      const [careTasks, setCareTasks] = useState(plant.care_tasks);
      const [showAddTaskForm, setShowAddTaskForm] = useState(false);
      const [editedTask, setEditedTask] = useState(null);

      const handleCheckboxClick = (id) => {
        setCareTasks((prevTasks) =>
          prevTasks.map((task) =>
            task.id === id ? { ...task, completed: !task.completed } : task
          )
        );
      };

      const [newTask, setNewTask] = useState({
        name: '',
        description: '',
        due_date: '',
      });

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewTask((prevTask) => ({ ...prevTask, [name]: value }));
      };

      const toggleAddTaskForm = () => {
        setShowAddTaskForm(!showAddTaskForm);
      };

          //  add task
      const handleAddTask = () => {
        fetch('http://localhost:9292/care_tasks', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            name: newTask.name,
            description: newTask.description,
            due_date: newTask.due_date,
            plant_id: plant.id,
          }),
        })
          .then((response) => response.json())
          .then((createdTask) => {
            setCareTasks((prevTasks) => [...prevTasks, createdTask]);
            toggleAddTaskForm();
          })
          .catch((error) => {
            console.error('Error adding care task:', error);
          });
      };

          // delete task
      const handleDeleteTask = (taskId) => {
        fetch(`http://localhost:9292/care_tasks/${taskId}`, {
          method: 'DELETE',
        })
          .then((response) => response.json())
          .then((data) => {
            if (data.message === 'Care task deleted successfully.') {
              setCareTasks((prevTasks) =>
                prevTasks.filter((task) => task.id !== taskId)
              );
            }
          })
          .catch((error) => console.error('Error deleting care task:', error));
      };

          // edit task
      const handleEditTask = () => {
        if (editedTask) {
          fetch(`http://localhost:9292/care_tasks/${editedTask.id}`, {
            method: 'PATCH',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              name: editedTask.name,
              description: editedTask.description,
              due_date: editedTask.due_date,
            }),
          })
            .then((response) => response.json())
            .then((updatedTask) => {
              setCareTasks((prevTasks) =>
                prevTasks.map((task) =>
                  task.id === updatedTask.id ? updatedTask : task
                )
              );
              setEditedTask(null);
            })
            .catch((error) => {
              console.error('Error updating care task:', error);
            });
        }
      };

      const handleEditIconClick = (task) => {
        setEditedTask({ ...task });
      };

      return (
        <div className="plant-details">
          <button className="cancel-button" onClick={onCancel}>
            <i className="bx bx-x"></i>
          </button>
          <div className="plant-image">
            <img src={plant.image_url} alt={plant.name} />
          </div>
          <div className="plant-info">
            <h2>{plant.name}</h2>
            <p>Species: {plant.species}</p>
            <h3>Care Tasks</h3>

            <button onClick={toggleAddTaskForm} className="add-task-button">
              Add Task
            </button>
            <ul>
              {careTasks.map((task) => (
                <li key={task.id} className={`task-item-${task.id}`}>
                  <div className="task-item">
                    <input
                      type="checkbox"
                      checked={task.completed}
                      onChange={() => handleCheckboxClick(task.id)}
                    />
                    <span
                      style={{
                        textDecoration: task.completed ? 'line-through' : 'none',
                      }}
                    >
                      {task.name}
                      <i
                        className="bx bx-edit-alt"
                        style={{ marginLeft: '10px', cursor: 'pointer' }}
                        onClick={() => handleEditIconClick(task)}
                      ></i>
                      <i
                        className="bx bx-trash"
                        style={{ marginLeft: '10px', cursor: 'pointer' }}
                        onClick={() => handleDeleteTask(task.id)}
                      ></i>
                    </span>
                  </div>
                  <div className="task-description">
                    <p>{task.description}</p>
                    <p>By {task.due_date}</p>
                  </div>
                </li>
              ))}
            </ul>
          </div>

          {showAddTaskForm && (
            <div className="task-form">
              <h3>Add New Care Task</h3>
              <form>
                <div className="form-group">
                  <label>Name:</label>
                  <input
                    type="text"
                    name="name"
                    value={newTask.name}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="form-group">
                  <label>Description:</label>
                  <input
                    type="text"
                    name="description"
                    value={newTask.description}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="form-group">
                  <label>Due Date:</label>
                  <input
                    type="date"
                    name="due_date"
                    value={newTask.due_date}
                    onChange={handleInputChange}
                  />
                </div>
                <button type="button" onClick={handleAddTask}>
                  Save
                </button>
                <button type="button" onClick={toggleAddTaskForm}>
                  Cancel
                </button>
              </form>
            </div>
          )}

          {editedTask && (
            <div className="task-form">
              <h3>Edit Care Task</h3>
              <form>
                <div className="form-group">
                  <label>Name:</label>
                  <input
                    type="text"
                    name="name"
                    value={editedTask.name}
                    onChange={(e) =>
                      setEditedTask({ ...editedTask, name: e.target.value })
                    }
                  />
                </div>
                <div className="form-group">
                  <label>Description:</label>
                  <input
                    type="text"
                    name="description"
                    value={editedTask.description}
                    onChange={(e) =>
                      setEditedTask({ ...editedTask, description: e.target.value })
                    }
                  />
                </div>
                <div className="form-group">
                  <label>Due Date:</label>
                  <input
                    type="date"
                    name="due_date"
                    value={editedTask.due_date}
                    onChange={(e) =>
                      setEditedTask({ ...editedTask, due_date: e.target.value })
                    }
                  />
                </div>
                <button type="button" onClick={handleEditTask}>
                  Save Changes
                </button>
                <button type="button" onClick={() => setEditedTask(null)}>
                  Cancel
                </button>
              </form>
            </div>
          )}
        </div>
    );
  }

export default PlantDetails;
